import React, { useState } from 'react';

const CategorySelection = ({ onCategorySelect }) => {
  const [currentStep, setCurrentStep] = useState('category');
  const [selections, setSelections] = useState({
    category: null,
    strain: null,
    experience: null,
    effects: null,
    price: null
  });

  // Categories data
  const categories = [
    {
      id: 'flower',
      name: 'Flower',
      hasStrains: true,
      icon: (
        <svg viewBox="0 0 24 24" className="w-full h-full" fill="none" stroke="currentColor" strokeWidth="1.5">
          <path d="M12 22C15.866 22 19 18.866 19 15C19 11.134 12 2 12 2C12 2 5 11.134 5 15C5 18.866 8.13401 22 12 22Z" />
        </svg>
      )
    },
    {
      id: 'pre-roll',
      name: 'Pre Rolls',
      hasStrains: true,
      icon: (
        <svg viewBox="0 0 24 24" className="w-full h-full" fill="none" stroke="currentColor" strokeWidth="1.5">
          <path d="M6 18L18 6" strokeLinecap="round" />
          <path d="M8 16L16 8" strokeLinecap="round" />
          <path d="M10 14L14 10" strokeLinecap="round" />
        </svg>
      )
    },
    {
      id: 'vaporizer',
      name: 'Vaporizers',
      hasStrains: true,
      icon: (
        <svg viewBox="0 0 24 24" className="w-full h-full" fill="none" stroke="currentColor" strokeWidth="1.5">
          <rect x="8" y="4" width="8" height="16" rx="2" />
          <path d="M10 8H14" />
          <path d="M12 12V16" />
        </svg>
      )
    },
    {
      id: 'concentrate',
      name: 'Concentrates',
      hasStrains: true,
      icon: (
        <svg viewBox="0 0 24 24" className="w-full h-full" fill="none" stroke="currentColor" strokeWidth="1.5">
          <circle cx="12" cy="12" r="4" />
          <path d="M12 8V6" />
          <path d="M16 12H18" />
          <path d="M12 16V18" />
          <path d="M8 12H6" />
        </svg>
      )
    },
    {
      id: 'edible',
      name: 'Edibles',
      hasStrains: true,
      icon: (
        <svg viewBox="0 0 24 24" className="w-full h-full" fill="none" stroke="currentColor" strokeWidth="1.5">
          <rect x="6" y="8" width="12" height="12" rx="1" />
          <path d="M8 8V7C8 5.89543 8.89543 5 10 5H14C15.1046 5 16 5.89543 16 7V8" />
          <path d="M8 12H16" />
          <path d="M8 16H16" />
        </svg>
      )
    },
    {
      id: 'accessories',
      name: 'Accessories',
      hasStrains: false,
      icon: (
        <svg viewBox="0 0 24 24" className="w-full h-full" fill="none" stroke="currentColor" strokeWidth="1.5">
          <rect x="4" y="8" width="16" height="12" rx="2" />
          <path d="M8 8V6C8 4.89543 8.89543 4 10 4H14C15.1046 4 16 5.89543 16 6V8" />
          <circle cx="12" cy="14" r="2" />
        </svg>
      )
    }
  ];

  const strains = [
    { id: 'sativa', name: 'Sativa', color: '#f59e0b' },
    { id: 'indica', name: 'Indica', color: '#7c3aed' },
    { id: 'hybrid', name: 'Hybrid', color: '#10b981' }
  ];

  const experienceLevels = [
    { id: 'novice', name: 'Novice', color: '#60a5fa', description: 'New to cannabis' },
    { id: 'intermediate', name: 'Intermediate', color: '#818cf8', description: '1-3 years experience' },
    { id: 'experienced', name: 'Experienced', color: '#6366f1', description: '3+ years experience' }
  ];

  const effects = [
    { id: 'relaxed', name: 'Relaxation', icon: 'üåô', color: '#8b5cf6' },
    { id: 'energetic', name: 'Energy', icon: '‚ö°', color: '#f59e0b' },
    { id: 'creative', name: 'Creativity', icon: 'üé®', color: '#ec4899' },
    { id: 'focused', name: 'Focus', icon: 'üéØ', color: '#14b8a6' },
    { id: 'sleep', name: 'Sleep', icon: 'üí§', color: '#6366f1' },
    { id: 'pain', name: 'Pain Relief', icon: 'üè•', color: '#ef4444' }
  ];

  const priceRanges = [
    { id: 'budget', name: 'Budget', color: '#10b981', description: '$ Economy' },
    { id: 'mid', name: 'Mid-Range', color: '#f59e0b', description: '$$ Standard' },
    { id: 'premium', name: 'Premium', color: '#8b5cf6', description: '$$$ Top Shelf' }
  ];

  const handleSelection = (item) => {
    if (!item) return;

    setSelections(prev => ({
      ...prev,
      [currentStep]: item
    }));

    if (currentStep === 'category') {
      if (!item.hasStrains) {
        onCategorySelect?.(item.id);
        return;
      }
      setCurrentStep('strain');
    } else if (currentStep === 'strain') {
      setCurrentStep('experience');
    } else if (currentStep === 'experience') {
      setCurrentStep('effects');
    } else if (currentStep === 'effects') {
      setCurrentStep('price');
    } else if (currentStep === 'price') {
      onCategorySelect?.({
        category: selections.category?.id || '',
        strain: selections.strain?.id || '',
        experience: selections.experience?.id || '',
        effects: selections.effects?.id || '',
        price: item.id
      });
    }
  };

  const handleBack = () => {
    const previousSteps = {
      strain: 'category',
      experience: 'strain',
      effects: 'experience',
      price: 'effects'
    };
    setCurrentStep(previousSteps[currentStep]);
  };

  const stepConfig = {
    category: {
      items: categories,
      title: "What type of product are you interested in today?"
    },
    strain: {
      items: strains,
      title: "What strain are you looking for?"
    },
    experience: {
      items: experienceLevels,
      title: "What's your experience level?"
    },
    effects: {
      items: effects,
      title: "What effects are you looking for?"
    },
    price: {
      items: priceRanges,
      title: "What's your preferred price range?"
    }
  };

  const renderButton = (item) => {
    if (!item) return null;

    return (
      <button
        onClick={() => handleSelection(item)}
        className="flex flex-col items-center justify-center p-4 rounded-2xl bg-[#f0b442] hover:bg-[#ffd789] transition-all duration-300"
        style={{
          minWidth: '100px',
          height: '100px'
        }}
      >
        {item.icon && (
          <div className="w-6 h-6 mb-2 text-black">
            {item.icon}
          </div>
        )}
        {item.color && !item.icon && (
          <div 
            className="w-6 h-6 rounded-full mb-2"
            style={{ backgroundColor: item.color }}
          />
        )}
        <span className="text-sm font-medium text-black">
          {item.name}
        </span>
        {item.description && (
          <span className="text-xs text-black/60 mt-1 text-center">
            {item.description}
          </span>
        )}
      </button>
    );
  };

  return (
    <div className="w-full p-4">
      {/* Title and Back Button */}
      <div className="mb-6">
        {currentStep !== 'category' && (
          <div className="flex items-center gap-2 mb-2">
            <button
              onClick={handleBack}
              className="text-sm text-black/60 hover:text-black flex items-center gap-1"
            >
              ‚Üê Back
            </button>
            <span className="text-sm text-black/60">|</span>
            <span className="text-sm">
              {selections.category?.name || ''}
              {selections.strain?.name ? ` > ${selections.strain.name}` : ''}
            </span>
          </div>
        )}
        <h2 className="text-lg font-medium">
          {stepConfig[currentStep].title}
        </h2>
      </div>

      {/* Buttons */}
      <div className="flex flex-wrap gap-4">
        {stepConfig[currentStep].items.map(item => (
          <div key={item.id}>
            {renderButton(item)}
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategorySelection;
