import React from 'react';
import SmartRecommendations from './SmartRecommendations';
import CategorySelection from './CategorySelection';

const Message = ({ 
  message,
  isUser,
  recommendations,
  onProductSelect,
  onLike,
  onDislike,
  onCategorySelect,
  selectedCategory,
  selectedStrain
}) => {
  if (isUser) return null;

  return (
    <div className="w-full mb-4">
      <div className="bg-white text-black w-full p-4 rounded-lg shadow-md">
        {message.content && (
          <div className="text-sm mb-4">{message.content}</div>
        )}

        {message.showCategories && (
          <CategorySelection onCategorySelect={onCategorySelect} />
        )}

        {selectedCategory && selectedStrain && (
          <div className="text-sm font-semibold mt-4 mb-2 text-gray-600">
            {`${selectedCategory} - ${selectedStrain}`}
          </div>
        )}

        {recommendations?.length > 0 && (
          <SmartRecommendations
            recommendations={recommendations}
            onProductSelect={onProductSelect}
            onLike={onLike}
            onDislike={onDislike}
          />
        )}
      </div>
    </div>
  );
};

export default Message;
