import React from 'react';
import SmartRecommendations from './SmartRecommendations';

const Message = ({ 
  isUser,
  recommendations,
  onProductSelect,
  onLike,
  onDislike,
  selectedCategory,
  selectedStrain
}) => {
  if (isUser || !recommendations?.length) return null;

  return (
    <div className="w-full mb-4">
      <div className="bg-white text-black w-full p-4 rounded-lg shadow-md">
        {selectedCategory && selectedStrain && (
          <div className="text-sm font-semibold mb-2 text-gray-600">
            {`${selectedCategory} - ${selectedStrain}`}
          </div>
        )}
        <SmartRecommendations
          recommendations={recommendations}
          onProductSelect={onProductSelect}
          onLike={onLike}
          onDislike={onDislike}
        />
      </div>
    </div>
  );
};

export default Message;
